package com.thebigfun.booking_service.model;import com.thebigfun.booking_service.dto.BookingDTO;import org.springframework.stereotype.Component;import java.util.List;import java.util.stream.Collectors;@Componentpublic class BookingMapper {    public BookingDTO toDto(Booking booking) {        return BookingDTO.builder()                .id(booking.getId())                .numberOfTickets(booking.getNumberOfTickets())                .totalPrice(booking.getTotalPrice())                .reservationDate(booking.getReservationDate())                .notes(booking.getNotes())                .expirationDate(booking.getExpirationDate())                .status(booking.getStatus())                .paymentStatus(booking.getPaymentStatus())                .build();    }    public List<BookingDTO> toDtoList(List<Booking> bookings) {        return bookings.stream()                .map(this::toDto)                .collect(Collectors.toList());    }}