package com.thebigfun.event_service.service;import com.thebigfun.event_service.model.entity.Event;import java.util.List;public interface EventService {    public abstract Event createEvent(Event event);    public abstract List<Event> getAllEvents();    public abstract Event getEventById(Long event_id);    public abstract Event updateEvent(Long event_id, Event event);    public abstract void deleteEvent(Long event_id);    public abstract boolean existsEventByEventId(Long event_id);    List<Event> findEventsByOrganizerId(Long organizerId);}